Method.Implementation:
try{
	set myobj = ..%OpenId(id)
	set sc = myobj.%JSONExportToString(.json,map)
	if $$$ISERR(sc) $$$ThrowStatus(sc)
	return json
}
catch ex{
	throw ex
}

try{
	set myobj = ..%OpenId(id)
	set sc = myobj.%JSONExportToStream(.json,map)
	if $$$ISERR(sc) $$$ThrowStatus(sc)
	return json
}
catch ex{
	throw ex
}
